                    {
                        SumsOfTotalDeductedValue += Convert.ToDouble(DGVR.Cells["TotalDeductionsVals"].Value.ToString());
                    }
                }
                catch (Exception ex)
                {

                }

            }
            return SumsOfTotalDeductedValue;
        }

